/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {
    `java-library`
    `maven-publish`
}

repositories {
    mavenLocal()
    maven {
        url = uri("https://repo.maven.apache.org/maven2/")
    }
}

dependencies {
    api(libs.com.fasterxml.jackson.core.jackson.databind)
    api(libs.commons.cli.commons.cli)
    api(libs.org.ow2.asm.asm.tree)
    api(libs.org.jetbrains.kotlinx.kotlinx.metadata.jvm)
    testImplementation(libs.junit.junit)
}

group = "com.github.dart_lang.jnigen"
// Make it so the outer script doesn't have to track the version number
//version = "0.0.1-SNAPSHOT"
description = "ApiSummarizer"
java.sourceCompatibility = JavaVersion.VERSION_11

publishing {
    publications.create<MavenPublication>("maven") {
        from(components["java"])
    }
}

tasks.withType<JavaCompile>() {
    options.encoding = "UTF-8"
}

tasks.withType<Javadoc>() {
    options.encoding = "UTF-8"
}
